import React, { useState, useEffect } from 'react'
import './App.css'

// const SpeechRecognition =
//   window.SpeechRecognition || window.webkitSpeechRecognition
// // const mic = new window.SpeechRecognition() || window.webkitSpeechRecognition()

const SpeechRecognition =
  window.SpeechRecognition || window.webkitSpeechRecognition
const mic = new SpeechRecognition()

mic.continuous = true
mic.interimResults = true
mic.lang = 'en-US'

function App() {
  const [isListening, setIsListening] = useState(false)
  const [note, setNote] = useState(null)
  const [data, setData] = useState(null)
  const [offset, setOffset] = useState(0)
  const [savedNotes, setSavedNotes] = useState([])
  const [midCommand, setMidCommand] = useState(true)
  const [start, setStart] = useState(null)
  const [now, setNow] = useState(null)


  useEffect(() => {
    handleListen()
  }, [isListening])

  const handleListen = () => {
    if (isListening) {
      mic.start()
      mic.onend = () => {
        console.log('continue..')
        mic.start()
      }
    } else {
      mic.stop()
      mic.onend = () => {
        console.log('Stopped Mic on Click')
      }
    }
    mic.onstart = () => {
      console.log('Mics on')
    }


    mic.onresult = event => {
      let transcript = Array.from(event.results)
        .map(result => result[0])
        .map(result => result.transcript)
        .join('')
      transcript = transcript.substring(offset)
      console.log(transcript)

      if((transcript.indexOf("Remy") != -1) && midCommand == false){
        setMidCommand(true);
      }
      if((transcript.indexOf("Remy") != -1) && midCommand == true){ 
        setNote(transcript.substring(transcript.indexOf("Remy")+4))
      }
      mic.onerror = event => {
        console.log(event.error)
      }

      if(transcript.indexOf("done") != -1){
        setOffset(transcript.length)
        let command = transcript.substring((transcript.indexOf("Remy")+4),transcript.indexOf("done")-1)
        // setMidCommand(false);
        setNote('')
        console.log(command);
        // setNote(command)
        fetch('http://localhost:5001/text-input', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json'
          },
          body: JSON.stringify({ command })
        })
      }

    }
    // if(midCommand == false){
    //   mic.stop()
    //   console.log('restarting mic')
    //   mic.start()
    //   setMidCommand(true)
    // }

  }

  const handleSaveNote = () => {
    setSavedNotes([...savedNotes, note])
    // send 'note' field to the backend 
    fetch('http://localhost:5001/text-input', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify({ note })
    })
    setNote('')
  }

  return (
    <>
      <h1>Voice Notes</h1>
      <div className="container">
        <div className="box">
          <h2>Current Note</h2>
          {isListening ? <span>üõëüéôÔ∏è</span> : <span>üéôÔ∏è</span>}
          <button onClick={handleSaveNote} disabled={!note}>
            Save Note
          </button>
          {/* <div onLoad={() => setIsListening(true)}> </div> */}
          <button onClick={() => setIsListening(prevState => !prevState)}>
            Start/Stop
          </button>
          <p>{note}</p>
        </div>
        <div className="box">
          <h2>Notes</h2>
          {savedNotes.map(n => (
            <p key={n}>{n}</p>
          ))}
        </div>
      </div>
    </>
  )
}

export default App
