import React, { useState, useEffect } from 'react'
import './App.css'

// const SpeechRecognition =
//   window.SpeechRecognition || window.webkitSpeechRecognition
// // const mic = new window.SpeechRecognition() || window.webkitSpeechRecognition()

const SpeechRecognition =
  window.SpeechRecognition || window.webkitSpeechRecognition
const mic = new SpeechRecognition()

mic.continuous = true
mic.interimResults = true
mic.lang = 'en-US'

function App() {
  const [isListening, setIsListening] = useState(false)
  const [note, setNote] = useState(null)
  const [savedNotes, setSavedNotes] = useState([])

  useEffect(() => {
    handleListen()
  }, [isListening])

  const handleListen = () => {
    if (isListening) {
      mic.start()
      mic.onend = () => {
        console.log('continue..')
        mic.start()
      }
    } else {
      mic.stop()
      mic.onend = () => {
        console.log('Stopped Mic on Click')
      }
    }
    mic.onstart = () => {
      console.log('Mics on')
    }

    mic.onresult = event => {
      const transcript = Array.from(event.results)
        .map(result => result[0])
        .map(result => result.transcript)
        .join('')
      console.log(transcript)
      if(transcript.indexOf("remy") != -1 || transcript.indexOf("remi") != -1 || transcript.indexOf("remmy") != -1 || transcript.indexOf("remmi") != -1){
        // transcript = transcript.substring(transcript.indexOf("listen")+6)
        setNote(transcript.substring(transcript.indexOf("rem")+6))

      }
      mic.onerror = event => {
        console.log(event.error)
      }
      mic.onspeechend = () => {
        //this works it just takes forever
        console.log("done listening 111")
      }
      mic.onend = () => {
        console.log("done listening 222")
      }
    }
  }

  const handleSaveNote = () => {
    setSavedNotes([...savedNotes, note])
    // send 'note' field to the backend 
    fetch('http://localhost:5001/text-input', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify({ note })
    })
    setNote('')
  }

  return (
    <>
      <h1>Voice Notes</h1>
      <div className="container">
        <div className="box">
          <h2>Current Note</h2>
          {isListening ? <span>üõëüéôÔ∏è</span> : <span>üéôÔ∏è</span>}
          <button onClick={handleSaveNote} disabled={!note}>
            Save Note
          </button>
          {/* <div onLoad={() => setIsListening(true)}> </div> */}
          <button onClick={() => setIsListening(prevState => !prevState)}>
            Start/Stop
          </button>
          <p>{note}</p>
        </div>
        <div className="box">
          <h2>Notes</h2>
          {savedNotes.map(n => (
            <p key={n}>{n}</p>
          ))}
        </div>
      </div>
    </>
  )
}

export default App
